select citation.cid, paper.pid as RefToPid, paper.main_author as RefToMainAuth, paper.main_author_id as RefToMainAuthId, paper.year_of_pub as RefToYOP, paper.paper_title RefToTitle, paper.publication_venue as RefToVenue, paper.abstract as RefToAbstract, citation.ref_by RefBypid, (select main_author from paper where pid=citation.ref_by) as RefByMainAuth, (select main_author_id from paper where pid=citation.ref_by) as RefByMainAuthId, (select year_of_pub from paper where pid=citation.ref_by) as RefByYOP, (select paper_title from paper where pid=citation.ref_by) as RefByTitle, (select publication_venue from paper where pid=citation.ref_by) as RefByPubVenue, (select abstract from paper where pid=citation.ref_by) as RefByAbstract
from citation,paper 
where citation.ref_to=paper.pid